var oat = flow.get("outdoorAmbientTemperature") || 21;

var temps = [{ "oat": "-20", "voltage": "9" }, { "oat": "-5", "voltage": "9" }, { "oat": "0", "voltage": "8" }, { "oat": "5", "voltage": "7.5" }, { "oat": "10", "voltage": "6.5" }, { "oat": "15", "voltage": "6" }, { "oat": "20", "voltage": "6" }, { "oat": "25", "voltage": "6" }, { "oat": "40", "voltage": "6" }]

if (flow.get("tOutSettings")) { temps = flow.get("tOutSettings"); }


var temp = 7;
for (var i = 1; i < temps.length; i++) {

    var t1 = parseFloat(temps[i - 1].oat);
    var t2 = parseFloat(temps[i].oat);
    var v1 = parseFloat(temps[i - 1].voltage);
    var v2 = parseFloat(temps[i].voltage);


    if (t2 == oat) {

        temp = v2;

        break;
    }
    else if (t1.oat == oat) {

        temp = v1;

        break;
    }
    else if ((t1 <= oat) && (t2 > oat)) {

        temp = v1 + ((v2 - v1) * ((oat - t1) / (t2 - t1)));

        break;
    }
    //else if ((t1 >= oat) && (t2 < oat)) {

    //     temp = v2 - ((v2 - v1) * ((oat - t1) / (t1 - t2)));

    //     break;
    // }

}

msg.payload = temp;

// if (oat < 0) { msg.payload = 9; }
