        var lns = ln.split(".")[3];
        if(gtype=="grapht" && lns.substr(0,1)!="t" && lns.substr(0,1)!="T") { dothis = 0; continue;}
        else if(gtype=="graphp" && lns.substr(0,1)!="p") { dothis = 0; continue;}
        else if(gtype=="graphf" && lns.substr(0,1)!="f") { dothis = 0; continue;}
        else if(gtype=="graphkwh" && lns.substr(0,3)!="kwh") { dothis = 0; continue;}
        else if(gtype=="graphkw" && (lns.substr(0,2)!="kw" || lns.substr(0,3)=="kwh")) { dothis = 0; continue;}

    }

    if (dothis==1) {

        selectedGraph += "," + ln.split(".")[3] + ",";

        var dat = global.get("readingsHistory." + ln) || {};

        var itemData = [];
        for (var readin in dat) {

            itemData.push({"x":dat[readin].timestamp, "y":dat[readin].value});

        }

        gdat0.series.push(ln.split(".")[3]);
        gdat0.data.push(itemData);
    }

}

