function uniq(a) {
    var seen = {};
    return a.filter(function(item) {
        return seen.hasOwnProperty(item) ? false : (seen[item] = true);
    });
}

if (msg.payload.indexOf(",")<1) { return null; }

var invar = msg.payload.split(",");

var presult = global.get("portSetup").filter(port => port.portId == invar[0]);
if (presult.length>0) { 

    var msg1 = {};

    var readin = parseFloat(invar[2].split(" ")[0]);

    if (presult[0].signalType && presult[0].signalType.indexOf("PULSE") == 0) {

        var p1 = flow.get("c" + invar[0] + "last") || 0;
        flow.set("c" + invar[0] + "last",readin);
        readin = readin - p1;
    }

    msg1.topic = (presult[0].topicDevice?presult[0].topicDevice + "/":"") + "dat/" + (presult[0].topicKey || "value"),
    msg1.payload = readin;
    msg1.points = 1000;
    msg1.units = presult[0].mapUnits;
    msg1.title = (presult[0].topicDevice?presult[0].topicDevice + " ":"") + presult[0].wiredToPort;

    var msg2 = {};
    msg2.topic = "raw/" + invar[0],
    msg2.payload = readin;
    msg2.title = presult[0].name || ("Raw " + invar[0]);
    if (presult[0].mapUnits) { msg2.units = presult[0].mapUnits.split(" to ")[0];  }

    var msg3 = { "payload":"ok"};  // alarm
    msg3.topic="alarm/terminal" + presult[0].portId;



    if (invar[1].substr(0, 5) == "rthrd" && readin==60) {  // sensor not connected

        msg1 = null;
        msg3.payload = "Sensor Disconnected"

    }

    else if (invar[1].substr(0, 5) == "rthrd" && readin == 30) {  // sensor not connected

        msg1 = null;
        msg3.payload = "Sensor Short-circuit"

    }

    else if(presult[0]["map"]) {

        msg1.units = presult[0].mapUnits.split(" to ")[1];   
        msg1.payload = "";

        var ntc = JSON.parse(presult[0]["map"]);

        var temp = -999;
        for (var i = 1; i < ntc.length; i++) { 

            if  (ntc[i][0]==readin) { 

                temp = ntc[i][1];

                break;
            }
            else if  (ntc[i-1][0]==readin) { 

                temp = ntc[i-1][1];

                break;
            }
            else if  ((ntc[i-1][0]<=readin) && (ntc[i][0]>readin)) { 

                temp = ntc[i-1][1] - ((ntc[i][1] - ntc[i-1][1]) * ((readin - ntc[i-1][0]) / (ntc[i-1][0] - ntc[i][0])));

                break;
            }
            else if  ((ntc[i-1][0]>=readin) && (ntc[i][0]<readin)) { 

                temp = ntc[i-1][1] - ((ntc[i][1] - ntc[i-1][1]) * ((readin - ntc[i-1][0]) / (ntc[i-1][0] - ntc[i][0])));

                break;
            }

        }


        if (presult[0].signalType && presult[0].signalType.indexOf("PULSE") == 0) {

            //temp = 1.111;
            if (temp == -999) { //} && (ntc[ntc.length -1][0] > readin)) {

                temp = ((ntc[1][1]) * ((readin) / (ntc[1][0])));
                //temp = ntc.length;

            }
        }

        if (temp != -999) {

            msg1.payload =  temp; //parseInt(temp*100)/100; 

            // if (temp <10) { msg1.payload =  parseInt(temp*100)/100; }
            // else { msg1.payload =  parseInt(temp*10)/10; }

            if (presult[0].signalType && (presult[0].signalType.indexOf("NTC")==0) && readin==60) {

                msg3.payload = "Out of range"

            }


        } else {

            //msg1.payload = 0;
            msg1 = null;
            msg3.payload = "Out of range"
        }
    }

    if (msg1) {


        var readout = parseInt(parseFloat(msg1.payload)*100)/100; 

        //var readout = 50;

        var history = context.get(invar[0]) || [];

        // if (history.length>5 && history.indexOf(msg1.payload)<0) {  history = [];  }
        // else if (history.length>1) {
        //     var uniqh = uniq(history);
        //     if ((uniqh.length<4) && (history.length<10)) {  
        //         // slow mode
        //         msg1=null; 

        //     } else {
        //     // else if (uniqh.length>0) {   readin = history.reduce((a, b) => a + b, 0) / history.length;  }
        //         msg1.payload = history.reduce((a, b) => a + b, 0) / history.length; 
        //         msg1.payload =  parseInt(msg1.payload*100)/100; 
        //     }
        //     if (history.length>9) { history=[msg1.payload]; }

        // }

        if (invar[1].substr(0, 5) == "rthrd" && history.indexOf(readout)>-1) {  

            msg1.payload =  parseInt((history.reduce((a, b) => a + b, 0) / history.length)*100)/100; 

        } //msg1=null;

        history.push(readout);
        if (history.length>20) { history.shift(); }
        context.set(invar[0],history);

    }   

    if (msg1) { msg1.payload = parseInt(msg1.payload * 100) / 100;  }

    return [msg1, msg2, msg3];

}

